function kanma (number) {
  var number = parseFloat(number);
  var number1 = number * 100;
  // //
  number1 = Math.round(number1) / 100;
  var numData = number1.toString().split('.');
  // 3
  numData[0] = Number(numData[0]).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  // 
  return numData.join('.');
};
